//import ProjectCard from "./ProjectCard";
import {React, useState} from "react"
import { IconContext } from "react-icons"
import {IoMdArrowDropleft, IoMdArrowDropright} from "react-icons/io"

const CardCarousel = ({projectCards}) => {

    const [currentProjectCard, setCurrentProjectCard] = useState(0)

    const prevProjectCard = () => {
        if (currentProjectCard < 1) {
            setCurrentProjectCard(projectCards.length - 1)
        }
        else {
            setCurrentProjectCard(currentProjectCard - 1)
        }
    }

    const nextProjectCard = () => {
        if (currentProjectCard >= projectCards.length-1) {
            setCurrentProjectCard(0)
        }
        else {
            setCurrentProjectCard(currentProjectCard + 1)
        }
    }
    return (
        <div className="main border-2 pt-2">
            <div className="carouselNavigation flex justify-center">
                <IconContext.Provider value={{size: "1.75em"}}>
                    <div className="hover:cursor-pointer" onClick={prevProjectCard}>
                        <IoMdArrowDropleft/>
                    </div>
                </IconContext.Provider>
                <IconContext.Provider value={{size: "1.75em"}}>
                    <div className="hover:cursor-pointer" onClick={nextProjectCard}>
                        <IoMdArrowDropright/>
                    </div>
                </IconContext.Provider>
            </div>
            <div className="cardContainer border-2 px-10 pt-2 h-fit">
                {projectCards[currentProjectCard]}
            </div>
        </div>
    )
}

export default CardCarousel
