import React, { useState } from 'react'
import * as Icon from 'react-bootstrap-icons'

function NavItem(props) {
  return(
    <li><a href="#" className="nav-item">{props.title}</a></li>
  )
}


export let NavBar = (props) => {

  const [toggleMenu, setToggleMenu] = useState(false);
  const [screenWidth, setScreenWidth] = useState(window.innerWidth);


  const toggleNav = () => {
    setToggleMenu(!toggleMenu)
  }

  return(
      <nav className="mx-auto pl-auto bg-blue-900">
        <div>
          <div className='lg:hidden px-2 pt-2'>
            <button onClick={toggleNav} className="text-white hover:text-blue-500"><Icon.List size={50}/></button>
          </div>
          <div className="flex justify-center pt-5">
            <ul className='lg:flex p-2'>
                <li className='px-2 pb-1 text-white'><a className="pb-2 px-2 lg:hover:border-b-4 border-white ease-linear duration-75" href="#">Home</a></li>
                <li className='px-2 pb-1 text-white'><a className="pb-2 px-2 lg:hover:border-b-4 border-white ease-linear duration-75" href="#">Projects</a></li>
                <li className='px-2 pb-1 text-white'><a className="pb-2 px-2 lg:hover:border-b-4 border-white ease-linear duration-75" href="#">About</a></li>
                <li className='px-2 pb-1 text-white'><a className="pb-2 px-2 lg:hover:border-b-4 border-white ease-linear duration-75" href="#">Contact</a></li>
              </ul>
          </div>
        </div>
      </nav>
  )
}
